          mobileNo: 1,
          loanAmount: 1,
          roi: 1,
          tenure: 1,
          emi: 1,
          executiveName: 1,
          createdAt: 1,
          updatedAt: 1,
          "applicantDetail.fullName": 1,
          "applicantDetail.fatherName": 1,
          "applicantDetail.mobileNo": 1,
