          employeId: 1,
          productId: 1,
          customerFinId: 1,
          mobileNo: 1,
          executiveName: 1,
          "externalVenderData.customerId": 1,
          "externalVenderData.statusByCreditPd": 1,
          "externalVenderData.fileStatus": 1,
